pragma solidity ^0.8.11;
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "./SocotraBranchManager.sol";

contract SocotraFactory {
    using Address for address;
    address immutable socotraBranchManagerImplementation;
    struct BranchInfo {
        address branchAddr;
        address parentToken;
        address issuer;
    }

    uint256 branchIds;
    uint256 MIN_ISSUE_AMOUNT = 0;

    mapping(uint256 => BranchInfo) branches;

    event SplitBranch(
        address branchAddr,
        address parentToken,
        uint256 amount,
        address issuer,
        uint256 branchId
    );

    constructor() {
        socotraBranchManagerImplementation = address(
            new SocotraBranchManager()
        );
    }

    /// @dev create new branch for splint up subdao
    /// @param parentToken address of ERC20 token
    /// @param amount initial amount of parent token
    /// @param name name of subdao
    /// @param imageUrl link to url of subdao's image
    /// @param tokenName name of subdao token
    /// @param tokenSymbol symbol of subdao token
    function splitBranch(
        address parentToken,
        uint256 amount,
        string memory name,
        string memory imageUrl,
        string memory tokenName,
        string memory tokenSymbol
    ) public returns (address) {
        require(amount > MIN_ISSUE_AMOUNT, "MUST_GREATER_THAN_MINIMUM");
        SocotraBranchManager branch = SocotraBranchManager(
            Clones.clone(socotraBranchManagerImplementation)
        );
        branch.init(
            parentToken,
            msg.sender,
            name,
            imageUrl,
            tokenName,
            tokenSymbol
        );

        branches[branchIds] = BranchInfo({
            branchAddr: address(branch),
            parentToken: parentToken,
            issuer: msg.sender
        });
        IERC20(parentToken).transferFrom(msg.sender, address(branch), amount);
        emit SplitBranch(
            address(branch),
            parentToken,
            amount,
            msg.sender,
            branchIds
        );
        branchIds++;
        return address(branch);
    }
}
","keccak256":"0x09082049275a13131190a5e8bbd2ba0c693e1b617f94e83f8a8f6694b17c932d","license":"ISC"},"contracts/SocotraVoteToken.sol":{"content":"// SPDX-License-Identifier: ISC

pragma solidity ^0.8.11;
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-ERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Votes.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract SocotraVoteToken is ERC20, ERC20Permit, ERC20Votes, Ownable {
    constructor(string memory _name, string memory _symbol)
        ERC20(_name, _symbol)
        ERC20Permit(_name)
    {}

    // The functions below are overrides required by Solidity.

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Votes) {
        super._afterTokenTransfer(from, to, amount);
    }

    function _mint(address to, uint256 amount)
        internal
        override(ERC20, ERC20Votes)
    {
        super._mint(to, amount);
    }

    function _burn(address account, uint256 amount)
        internal
        override(ERC20, ERC20Votes)
    {
        super._burn(account, amount);
    }

    /// @dev burn token by manager
    /// @param target target address
    /// @param amount amount to burn
    function _managerBurn(address target, uint256 amount) external onlyOwner {
        _burn(target, amount);
    }

    /// @dev mint token by manager
    /// @param target target address
    /// @param amount amount to mint
    function _managerMint(address target, uint256 amount) external onlyOwner {
        _mint(target, amount);
    }
}
